export default {
  props: {
    channels: {
      type: Array,
      required: true
    },
    initialChannel: {
      type: Object,
      default: () => ({
        title: '',
        code: '',
        mailing: 'Telegram Bot',
        disc: '',
        json: '{ "telegram_token":""}',
        status: true
      })
    }
  },
  data() {
    return {
      channel: {
        ...this.initialChannel
      },
      // Создаем локальную копию объекта initialChannel
      errors: {
        title: '',
        code: ''
      }
    };
  },
  methods: {
    getNextId() {
      if (this.channels.length === 0) {
        return 1;
      }
      const maxId = Math.max(...this.channels.map(channel => channel.id));
      return maxId + 1;
    },
    createChannel() {
      this.clearErrors();
      if (!this.channel.title) {
        this.errors.title = 'Название обязательно для заполнения';
      }
      if (!this.channel.code) {
        this.errors.code = 'Уникальный код обязательно для заполнения';
      }
      if (this.errors.title || this.errors.code) {
        return;
      }
      const newChannelId = this.getNextId();
      const newChannel = {
        ...this.channel,
        id: newChannelId
      };
      this.$emit('create', newChannel);
      this.resetForm();
    },
    clearErrors() {
      this.errors = {
        title: '',
        code: ''
      };
    },
    resetForm() {
      this.channel = {
        ...this.initialChannel // Сбрасываем форму до начального состояния initialChannel
      };
    }
  },
  watch: {
    initialChannel: {
      deep: true,
      handler(newVal) {
        // Обновляем локальную копию channel при изменении initialChannel извне
        this.channel = {
          ...newVal
        };
      }
    }
  }
};
